mapper.select:
SELECT t1.fee_rule_id, t1.fee_rule_code, t1.system_subject, t1.standard_proportion, t1.budget_proportion, t1.amount, t3.is_level FROM report_cost_breakdown_rule t1 INNER JOIN report_cost_breakdown_rule_dept_rel t2 ON t1.fee_rule_id = t2.rule_id INNER JOIN system_subject_basic_info t3 ON t1.fee_rule_code = t3.system_subject_code
AND t1.is_deleted = 0 AND t1.is_enable = 1
AND t1.con_id = #{conId}
AND t2.dept_id = #{deptId}

SELECT t1.payment_id t2.fee_rule_id, t2.con_id, t2.fee_rule_code, t2.system_subject, t2.standard_proportion, t2.budget_proportion, t1.allocat_amount, t2.amount FROM payment_collect_result t1 INNER JOIN report_cost_breakdown_rule t2 ON t1.fee_rule_id = t2.fee_rule_id AND t2.is_deleted = 0
AND t1.is_deleted = 0
AND t1.payment_id = #{paymentId}
